import React, { useState } from 'react';

function Categories() {
  // Sample categories data - in a real app this would come from an API
  const [categories, setCategories] = useState([
    { id: 1, name: 'Clothes', description: 'All clothing items', productCount: 15, status: 'Active' },
    { id: 2, name: 'Perfume', description: 'Fragrances and scents', productCount: 8, status: 'Active' },
    { id: 3, name: 'Electronics', description: 'Electronic devices and accessories', productCount: 12, status: 'Inactive' },
    { id: 4, name: 'Books', description: 'Books and publications', productCount: 25, status: 'Active' },
    { id: 5, name: 'Food & Beverages', description: 'Food and drink items', productCount: 30, status: 'Active' },
  ]);

  const [showAddModal, setShowAddModal] = useState(false);
  const [editingCategory, setEditingCategory] = useState(null);
  const [formData, setFormData] = useState({
    name: '',
    description: ''
  });

  const handleAddCategory = () => {
    if (formData.name.trim()) {
      const newCategory = {
        id: categories.length + 1,
        name: formData.name,
        description: formData.description,
        productCount: 0,
        status: 'Active'
      };
      setCategories([...categories, newCategory]);
      setFormData({ name: '', description: '' });
      setShowAddModal(false);
    }
  };

  const handleEditCategory = () => {
    if (editingCategory && formData.name.trim()) {
      setCategories(categories.map(cat => 
        cat.id === editingCategory.id 
          ? { ...cat, name: formData.name, description: formData.description }
          : cat
      ));
      setFormData({ name: '', description: '' });
      setEditingCategory(null);
    }
  };

  const handleDeleteCategory = (id) => {
    if (window.confirm('Are you sure you want to delete this category?')) {
      setCategories(categories.filter(cat => cat.id !== id));
    }
  };

  const openEditModal = (category) => {
    setEditingCategory(category);
    setFormData({ name: category.name, description: category.description });
  };

  const closeModal = () => {
    setShowAddModal(false);
    setEditingCategory(null);
    setFormData({ name: '', description: '' });
  };

  return (
    <div className="min-h-screen w-full bg-gradient-to-b from-[#f0f4ff] to-[#eaf0fb] py-8 px-2 sm:px-6 flex flex-col items-center">
      {/* Header */}
      <div className="w-full max-w-6xl flex items-center justify-between mb-6">
        <h1 className="text-2xl font-bold text-gray-800">Category Management</h1>
        <button 
          onClick={() => setShowAddModal(true)}
          className="bg-[#5b6dfa] hover:bg-[#3b4cca] text-white px-5 py-2 rounded-lg font-semibold shadow transition flex items-center gap-2"
        >
          <span className="text-lg">＋</span>
          Add Category
        </button>
      </div>

      {/* Card/Table */}
      <div className="w-full max-w-6xl bg-white rounded-2xl shadow-lg p-0 overflow-hidden">
        <table className="min-w-full text-sm">
          <thead>
            <tr className="bg-[#f6f8fc] text-gray-500 uppercase text-xs">
              <th className="py-4 px-4 text-left font-semibold">Category</th>
              <th className="py-4 px-4 text-left font-semibold">Description</th>
              <th className="py-4 px-4 text-left font-semibold">Products</th>
              <th className="py-4 px-4 text-left font-semibold">Status</th>
              <th className="py-4 px-4 text-center font-semibold">Actions</th>
            </tr>
          </thead>
          <tbody>
            {categories.length === 0 && (
              <tr>
                <td colSpan={5} className="py-8 text-center text-gray-400">No categories found.</td>
              </tr>
            )}
            {categories.map((category) => (
              <tr
                key={category.id}
                className={
                  `transition ${category.status === 'Inactive' ? 'bg-red-50' : 'hover:bg-[#f6f8fc]'} border-b last:border-b-0`
                }
              >
                <td className="py-4 px-4 font-medium flex items-center gap-2">
                  <span className="inline-block bg-blue-100 text-blue-700 px-2 py-1 rounded-full text-xs font-semibold capitalize">
                    {category.name}
                  </span>
                </td>
                <td className="py-4 px-4 text-gray-600">{category.description}</td>
                <td className="py-4 px-4">
                  <span className="bg-purple-100 text-purple-700 px-2 py-1 rounded-full text-xs">
                    {category.productCount} items
                  </span>
                </td>
                <td className="py-4 px-4">
                  <span className={`px-2 py-1 rounded-full text-xs font-semibold ${
                    category.status === 'Active' ? 'bg-green-100 text-green-700' : 'bg-red-100 text-red-700'
                  }`}>
                    {category.status}
                  </span>
                </td>
                <td className="py-4 px-4 text-center">
                  <button
                    onClick={() => openEditModal(category)}
                    className="text-blue-500 hover:text-blue-700 p-2 rounded transition"
                    title="Edit"
                  >
                    <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15.232 5.232l3.536 3.536M9 13h3l8-8a2.828 2.828 0 00-4-4l-8 8v3zm0 0v3a1 1 0 001 1h3" /></svg>
                  </button>
                  <button
                    onClick={() => handleDeleteCategory(category.id)}
                    className="text-red-500 hover:text-red-700 p-2 rounded transition"
                    title="Delete"
                  >
                    <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" /></svg>
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Add/Edit Modal */}
      {(showAddModal || editingCategory) && (
        <div className="fixed inset-0 bg-black bg-opacity-40 flex items-center justify-center z-50 p-4">
          <div className="bg-white rounded-2xl p-8 w-full max-w-md shadow-xl">
            <h3 className="text-lg font-semibold mb-4">
              {editingCategory ? 'Edit Category' : 'Add New Category'}
            </h3>
            <div className="space-y-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Category Name *
                </label>
                <input
                  type="text"
                  value={formData.name}
                  onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                  className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-[#5b6dfa]"
                  placeholder="Enter category name"
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Description
                </label>
                <textarea
                  value={formData.description}
                  onChange={(e) => setFormData({ ...formData, description: e.target.value })}
                  className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-[#5b6dfa]"
                  rows="3"
                  placeholder="Enter category description"
                />
              </div>
            </div>
            <div className="flex gap-3 mt-6">
              <button
                onClick={editingCategory ? handleEditCategory : handleAddCategory}
                className="flex-1 bg-[#5b6dfa] hover:bg-[#3b4cca] text-white py-2 rounded-lg font-semibold transition"
              >
                {editingCategory ? 'Update Category' : 'Add Category'}
              </button>
              <button
                onClick={closeModal}
                className="flex-1 bg-gray-200 hover:bg-gray-300 text-gray-700 py-2 rounded-lg font-semibold transition"
              >
                Cancel
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default Categories;
