import React, { useState } from 'react';

const initialUsers = [
  {
    id: '1',
    username: 'admin',
    email: 'admin@example.com',
    full_name: 'Admin User',
    role: 'admin',
    is_active: true,
    last_login: '2024-06-01 10:00',
    created_at: '2024-05-01 09:00',
    updated_at: '2024-06-01 10:00',
  },
  {
    id: '2',
    username: 'manager1',
    email: 'manager1@example.com',
    full_name: 'Manager One',
    role: 'manager',
    is_active: true,
    last_login: '2024-06-02 11:00',
    created_at: '2024-05-10 09:00',
    updated_at: '2024-06-02 11:00',
  },
];

const roles = ['admin', 'manager', 'cashier', 'inventory'];

function Users() {
  const [users, setUsers] = useState(initialUsers);
  const [showAddModal, setShowAddModal] = useState(false);
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: '',
    full_name: '',
    role: 'cashier',
    is_active: true,
  });
  const [error, setError] = useState('');

  const handleAddUser = () => {
    // Basic validation
    if (!formData.username || !formData.email || !formData.password || !formData.full_name || !formData.role) {
      setError('All fields except password are required.');
      return;
    }
    if (!roles.includes(formData.role)) {
      setError('Invalid role.');
      return;
    }
    if (!/^[^@\s]+@[^@\s]+\.[^@\s]+$/.test(formData.email)) {
      setError('Invalid email address.');
      return;
    }
    // Check for unique username/email
    if (users.some(u => u.username === formData.username)) {
      setError('Username already exists.');
      return;
    }
    if (users.some(u => u.email === formData.email)) {
      setError('Email already exists.');
      return;
    }
    // Add user (password is not stored in plain text here)
    setUsers([
      ...users,
      {
        id: (users.length + 1).toString(),
        username: formData.username,
        email: formData.email,
        full_name: formData.full_name,
        role: formData.role,
        is_active: formData.is_active,
        last_login: null,
        created_at: new Date().toISOString(),
        updated_at: new Date().toISOString(),
      },
    ]);
    setFormData({ username: '', email: '', password: '', full_name: '', role: 'cashier', is_active: true });
    setShowAddModal(false);
    setError('');
  };

  return (
    <div className="flex flex-col w-full min-h-screen space-y-4 sm:space-y-6">
      {/* Header */}
      <div className="bg-white rounded-xl shadow p-4 sm:p-6 w-full">
        <div className="flex flex-col sm:flex-row items-center justify-between mb-4 sm:mb-6 gap-2 sm:gap-0">
          <h1 className="text-xl sm:text-2xl font-bold text-gray-800">User Management</h1>
          <button
            onClick={() => setShowAddModal(true)}
            className="bg-purple-600 hover:bg-purple-700 text-white px-4 py-2 rounded-lg font-medium transition flex items-center gap-2"
          >
            <span>‚ûï</span>
            Add User
          </button>
        </div>
        <p className="text-gray-600 text-sm sm:text-base">
          Manage users, roles, and access for your POS system.
        </p>
      </div>

      {/* Users List */}
      <div className="bg-white rounded-xl shadow p-4 sm:p-6 w-full">
        <div className="overflow-x-auto">
          <table className="min-w-full text-xs sm:text-sm">
            <thead>
              <tr className="text-left text-gray-500 border-b">
                <th className="py-3 px-3 font-semibold">Username</th>
                <th className="py-3 px-3 font-semibold">Full Name</th>
                <th className="py-3 px-3 font-semibold">Email</th>
                <th className="py-3 px-3 font-semibold">Role</th>
                <th className="py-3 px-3 font-semibold">Active</th>
                <th className="py-3 px-3 font-semibold">Last Login</th>
                <th className="py-3 px-3 font-semibold">Actions</th>
              </tr>
            </thead>
            <tbody>
              {users.map((user) => (
                <tr key={user.id} className="border-b hover:bg-gray-50">
                  <td className="py-3 px-3 font-medium">{user.username}</td>
                  <td className="py-3 px-3">{user.full_name}</td>
                  <td className="py-3 px-3">{user.email}</td>
                  <td className="py-3 px-3 capitalize">{user.role}</td>
                  <td className="py-3 px-3">
                    <span className={`px-2 py-1 rounded-full text-xs font-semibold ${user.is_active ? 'bg-green-100 text-green-700' : 'bg-gray-100 text-gray-700'}`}>{user.is_active ? 'Yes' : 'No'}</span>
                  </td>
                  <td className="py-3 px-3 text-xs">{user.last_login ? user.last_login : '-'}</td>
                  <td className="py-3 px-3">
                    <button className="text-blue-600 hover:text-blue-800 p-1" title="Edit" disabled>‚úèÔ∏è</button>
                    <button className="text-red-600 hover:text-red-800 p-1" title="Delete" disabled>üóëÔ∏è</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>

      {/* Add User Modal */}
      {showAddModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
          <div className="bg-white rounded-xl shadow-lg p-6 w-full max-w-md">
            <h2 className="text-xl font-bold mb-4">Add New User</h2>
            {error && <div className="text-red-600 text-sm mb-2">{error}</div>}
            <div className="space-y-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Username *</label>
                <input
                  type="text"
                  value={formData.username}
                  onChange={e => setFormData({ ...formData, username: e.target.value })}
                  className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-purple-500"
                  placeholder="Enter username"
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Email *</label>
                <input
                  type="email"
                  value={formData.email}
                  onChange={e => setFormData({ ...formData, email: e.target.value })}
                  className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-purple-500"
                  placeholder="Enter email"
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Password *</label>
                <input
                  type="password"
                  value={formData.password}
                  onChange={e => setFormData({ ...formData, password: e.target.value })}
                  className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-purple-500"
                  placeholder="Enter password"
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Full Name *</label>
                <input
                  type="text"
                  value={formData.full_name}
                  onChange={e => setFormData({ ...formData, full_name: e.target.value })}
                  className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-purple-500"
                  placeholder="Enter full name"
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Role *</label>
                <select
                  value={formData.role}
                  onChange={e => setFormData({ ...formData, role: e.target.value })}
                  className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-purple-500"
                >
                  {roles.map(role => (
                    <option key={role} value={role}>{role.charAt(0).toUpperCase() + role.slice(1)}</option>
                  ))}
                </select>
              </div>
              <div className="flex items-center gap-2">
                <input
                  type="checkbox"
                  checked={formData.is_active}
                  onChange={e => setFormData({ ...formData, is_active: e.target.checked })}
                  id="is_active"
                  className="h-4 w-4 text-purple-600 border-gray-300 rounded"
                />
                <label htmlFor="is_active" className="text-sm text-gray-700">Active</label>
              </div>
            </div>
            <div className="flex gap-3 mt-6">
              <button
                onClick={handleAddUser}
                className="flex-1 bg-purple-600 hover:bg-purple-700 text-white py-2 rounded-lg font-medium transition"
              >
                Add User
              </button>
              <button
                onClick={() => { setShowAddModal(false); setError(''); }}
                className="flex-1 bg-gray-300 hover:bg-gray-400 text-gray-700 py-2 rounded-lg font-medium transition"
              >
                Cancel
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default Users;
