import React, { useState, useEffect } from 'react';
import { createSale } from '../services/saleService';

// SaleItemForm component (inline for this file)
function SaleItemForm({ products, onSubmit }) {
  const [form, setForm] = useState({
    product_id: '',
      quantity: 1,
    unit_price: '',
    discount_amount: 0,
    product_name: '',
    product_sku: '',
    product_barcode: '',
  });

  useEffect(() => {
    const selected = products.find(p => p.id === form.product_id || p._id === form.product_id);
    if (selected) {
      setForm(f => ({
        ...f,
        product_name: selected.name || '',
        product_sku: selected.sku || '',
        product_barcode: selected.barcode || '',
        unit_price: selected.price || '',
      }));
    }
    // eslint-disable-next-line
  }, [form.product_id]);

  const handleChange = e => {
    const { name, value } = e.target;
    setForm(f => ({ ...f, [name]: value }));
  };

  const total_price = (Number(form.quantity) * Number(form.unit_price)).toFixed(2);

  const handleSubmit = e => {
    e.preventDefault();
    onSubmit && onSubmit({ ...form, total_price });
    setForm({
      product_id: '',
      quantity: 1,
      unit_price: '',
      discount_amount: 0,
      product_name: '',
      product_sku: '',
      product_barcode: '',
    });
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-4 bg-gray-50 p-4 rounded mb-4">
      <div className="grid grid-cols-1 sm:grid-cols-3 gap-4">
        <div>
          <label className="block text-sm font-medium mb-1">Product</label>
          <select name="product_id" value={form.product_id} onChange={handleChange} required className="border rounded p-2 w-full">
            <option value="">Select product</option>
            {products.map(p => (
              <option key={p.id || p._id} value={p.id || p._id}>{p.name}</option>
            ))}
          </select>
        </div>
        <div>
          <label className="block text-sm font-medium mb-1">Quantity</label>
          <input type="number" name="quantity" value={form.quantity} min="1" onChange={handleChange} required className="border rounded p-2 w-full" />
        </div>
        <div>
          <label className="block text-sm font-medium mb-1">Unit Price</label>
          <input type="number" name="unit_price" value={form.unit_price} min="0" step="0.01" onChange={handleChange} required className="border rounded p-2 w-full" />
        </div>
      </div>
      <div className="grid grid-cols-1 sm:grid-cols-3 gap-4">
        <div>
          <label className="block text-sm font-medium mb-1">Discount Amount</label>
          <input type="number" name="discount_amount" value={form.discount_amount} min="0" step="0.01" onChange={handleChange} className="border rounded p-2 w-full" />
        </div>
        <div>
          <label className="block text-sm font-medium mb-1">Product Name</label>
          <input type="text" name="product_name" value={form.product_name} readOnly className="border rounded p-2 w-full bg-gray-100" />
        </div>
        <div>
          <label className="block text-sm font-medium mb-1">Product SKU</label>
          <input type="text" name="product_sku" value={form.product_sku} readOnly className="border rounded p-2 w-full bg-gray-100" />
        </div>
        <div>
          <label className="block text-sm font-medium mb-1">Product Barcode</label>
          <input type="text" name="product_barcode" value={form.product_barcode} readOnly className="border rounded p-2 w-full bg-gray-100" />
        </div>
        <div>
          <label className="block text-sm font-medium mb-1">Total Price</label>
          <input type="text" value={total_price} readOnly className="border rounded p-2 w-full bg-gray-100" />
        </div>
      </div>
      <div className="flex gap-2 mt-2">
        <button type="submit" className="bg-green-600 text-white px-4 py-2 rounded">Add Sale Item</button>
      </div>
    </form>
  );
}

// Helper to generate random 8-digit number as string
function generateSaleNumber() {
  return Math.floor(10000000 + Math.random() * 90000000).toString();
}

function SaleItems() {
  // Master state
  const [sale, setSale] = useState({
    sale_number: generateSaleNumber(),
    payment_method: 'cash',
    payment_status: 'completed',
    notes: '',
  });
  // Sale item state
  const [item, setItem] = useState({
    product_id: '',
    quantity: 1,
    unit_price: '',
    discount_amount: 0,
    product_name: '',
    product_sku: '',
    product_barcode: '',
  });
  const [products, setProducts] = useState([
    // Example products; replace with real fetch if needed
    { id: '1', name: 'Product A', sku: 'A001', barcode: '123456', price: 10.00 },
    { id: '2', name: 'Product B', sku: 'B002', barcode: '789012', price: 20.00 },
  ]);

  useEffect(() => {
    const selected = products.find(p => p.id === item.product_id || p._id === item.product_id);
    if (selected) {
      setItem(f => ({
        ...f,
        product_name: selected.name || '',
        product_sku: selected.sku || '',
        product_barcode: selected.barcode || '',
        unit_price: selected.price || '',
      }));
    }
    // eslint-disable-next-line
  }, [item.product_id]);

  const handleMasterChange = (e) => {
    const { name, value } = e.target;
    setSale((prev) => ({ ...prev, [name]: value }));
  };

  const handleItemChange = (e) => {
    const { name, value } = e.target;
    setItem(f => ({ ...f, [name]: value }));
  };

  const total_price = (Number(item.quantity) * Number(item.unit_price)).toFixed(2);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const saleData = {
      sale_number: sale.sale_number,
      payment_method: sale.payment_method,
      payment_status: sale.payment_status,
      notes: sale.notes,
      items: [
        {
          product_id: item.product_id,
      quantity: item.quantity,
      unit_price: item.unit_price,
      discount_amount: item.discount_amount,
        }
      ]
    };
    try {
      const response = await createSale(saleData);
      alert('Sale created! ID: ' + response.id);
      // Optionally reset form or redirect
    } catch (err) {
      alert('Error: ' + (err.response?.data?.error || err.message));
    }
  };

  return (
    <div className="flex flex-col w-full min-h-screen space-y-4 sm:space-y-6">
      {/* Header */}
      <div className="bg-white rounded-xl shadow p-4 sm:p-6 w-full">
        <div className="flex flex-col sm:flex-row items-center justify-between mb-4 sm:mb-6 gap-2 sm:gap-0">
          <h1 className="text-xl sm:text-2xl font-bold text-gray-800">Sale Entry</h1>
        </div>
        <p className="text-gray-600 text-sm sm:text-base">
          Create a new sale and add a sale item.
        </p>
      </div>

      {/* Form Card */}
      <form onSubmit={handleSubmit} className="space-y-6 w-full">
        {/* Sale Master Card */}
      <div className="bg-white rounded-xl shadow p-4 sm:p-6 w-full">
          <h2 className="text-lg font-bold text-blue-800 bg-blue-100 rounded px-3 py-1 mb-4 inline-block">Sale Information</h2>
          <div className="grid grid-cols-1 sm:grid-cols-2 gap-4">
            <div>
              <label className="block text-sm font-medium">Sale Number</label>
              <input type="text" name="sale_number" value={sale.sale_number} readOnly className="mt-1 w-full border rounded p-2 bg-gray-100 cursor-not-allowed" required />
                    </div>
            <div>
              <label className="block text-sm font-medium">Payment Method</label>
              <select name="payment_method" value={sale.payment_method} onChange={handleMasterChange} className="mt-1 w-full border rounded p-2">
                <option value="cash">Cash</option>
                <option value="card">Card</option>
                <option value="digital">Digital</option>
                <option value="gift_card">Gift Card</option>
                <option value="store_credit">Store Credit</option>
              </select>
        </div>
            <div>
              <label className="block text-sm font-medium">Payment Status</label>
              <select name="payment_status" value={sale.payment_status} onChange={handleMasterChange} className="mt-1 w-full border rounded p-2">
                <option value="pending">Pending</option>
                <option value="completed">Completed</option>
                <option value="refunded">Refunded</option>
                <option value="failed">Failed</option>
              </select>
            </div>
          </div>
          <div className="mt-4">
            <label className="block text-sm font-medium">Notes</label>
            <textarea name="notes" value={sale.notes} onChange={handleMasterChange} className="mt-1 w-full border rounded p-2" rows={2} />
          </div>
        </div>
        {/* Sale Item Card */}
        <div className="bg-white rounded-xl shadow p-4 sm:p-6 w-full">
          <h2 className="text-lg font-bold text-blue-800 bg-blue-100 rounded px-3 py-1 mb-4 inline-block">Sale Item Information</h2>
          <div className="grid grid-cols-1 sm:grid-cols-3 gap-4">
              <div>
              <label className="block text-sm font-medium mb-1">Product</label>
              <select name="product_id" value={item.product_id} onChange={handleItemChange} required className="border rounded p-2 w-full">
                <option value="">Select product</option>
                {products.map(p => (
                  <option key={p.id || p._id} value={p.id || p._id}>{p.name}</option>
                ))}
              </select>
              </div>
              <div>
              <label className="block text-sm font-medium mb-1">Quantity</label>
              <input type="number" name="quantity" value={item.quantity} min="1" onChange={handleItemChange} required className="border rounded p-2 w-full" />
              </div>
              <div>
              <label className="block text-sm font-medium mb-1">Unit Price</label>
              <input type="number" name="unit_price" value={item.unit_price} min="0" step="0.01" onChange={handleItemChange} required className="border rounded p-2 w-full" />
            </div>
              </div>
          <div className="grid grid-cols-1 sm:grid-cols-3 gap-4">
              <div>
              <label className="block text-sm font-medium mb-1">Discount Amount</label>
              <input type="number" name="discount_amount" value={item.discount_amount} min="0" step="0.01" onChange={handleItemChange} className="border rounded p-2 w-full" />
            </div>
            <div>
              <label className="block text-sm font-medium mb-1">Total Price</label>
              <input type="text" value={total_price} readOnly className="border rounded p-2 w-full bg-gray-100" />
            </div>
          </div>
        </div>
        {/* Submit Button */}
        <div className="flex justify-end mt-6">
          <button type="submit" className="bg-green-600 hover:bg-green-700 text-white px-6 py-2 rounded-lg font-semibold transition">
            Submit Sale
          </button>
        </div>
      </form>
    </div>
  );
}

export default SaleItems;
