import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const navItems = [
  { label: 'Dashboard', icon: 'ðŸ ' },
  { label: 'Customers', icon: 'ðŸ§‘â€ðŸ¤â€ðŸ§‘' },
  { label: 'Suppliers', icon: 'ðŸšš' },
  { label: 'Products', icon: 'ðŸ“¦' },
  { label: 'Stock In', icon: 'âž•' },
  { label: 'Stock Out (Sales)', icon: 'âž–' },
  { label: 'Reports', icon: 'ðŸ“Š' },
  { 
    label: 'Settings', 
    icon: 'âš™ï¸',
    hasDropdown: true,
    subItems: [
      { label: 'General Settings', icon: 'ðŸ”§' },
      { label: 'Add Categories', icon: 'ðŸ‘¥' },
      { label: 'Security', icon: 'ðŸ”’' },
      { label: 'Backup & Restore', icon: 'ðŸ’¾' },
      { label: 'System Updates', icon: 'ðŸ”„' },
      { label: 'API Configuration', icon: 'ðŸ”—' }
    ]
  },
];

function Sidebar({ open, onClose }) {
  const [openDropdown, setOpenDropdown] = useState(null);

  const toggleDropdown = (label) => {
    setOpenDropdown(openDropdown === label ? null : label);
  };

  const renderNavItem = (item) => {
    const isDropdownOpen = openDropdown === item.label;

    return (
      <li key={item.label}>
        <button
          onClick={() => item.hasDropdown ? toggleDropdown(item.label) : null}
          className="w-full flex items-center justify-between gap-3 px-3 py-2 rounded-lg text-gray-700 hover:bg-purple-100 hover:text-purple-700 font-medium transition"
        >
          <div className="flex items-center gap-3">
            <span className="text-lg">{item.icon}</span>
            <span>{item.label}</span>
          </div>
          {item.hasDropdown && (
            <svg 
              className={`w-4 h-4 transition-transform duration-200 ${isDropdownOpen ? 'rotate-180' : ''}`}
              fill="none" 
              stroke="currentColor" 
              viewBox="0 0 24 24"
            >
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
            </svg>
          )}
        </button>

        {item.hasDropdown && isDropdownOpen && (
          <ul className="ml-6 mt-2 space-y-1">
            {item.subItems.map((subItem) => (
              <li key={subItem.label}>
                {subItem.label === 'Add Categories' ? (
                  <Link
                    to="/categories"
                    className="flex items-center gap-3 px-3 py-2 rounded-lg text-gray-600 hover:bg-purple-50 hover:text-purple-700 text-sm transition"
                  >
                    <span className="text-sm">{subItem.icon}</span>
                    <span>{subItem.label}</span>
                  </Link>
                ) : (
                  <a
                    href="#"
                    className="flex items-center gap-3 px-3 py-2 rounded-lg text-gray-600 hover:bg-purple-50 hover:text-purple-700 text-sm transition"
                  >
                    <span className="text-sm">{subItem.icon}</span>
                    <span>{subItem.label}</span>
                  </a>
                )}
              </li>
            ))}
          </ul>
        )}
      </li>
    );
  };

  return (
    <>
      {/* Mobile sidebar (overlay) */}
      <div
        className={`fixed inset-y-0 left-0 z-40 w-56 bg-white shadow-lg flex flex-col py-8 px-4 transform transition-transform duration-200 md:hidden
          ${open ? 'translate-x-0' : '-translate-x-full'}`}
        style={{ willChange: 'transform' }}
      >
        <div className="mb-8 flex items-center justify-between">
          <span className="text-2xl font-bold text-purple-700 tracking-wide">StockMS</span>
          <button onClick={onClose} className="text-gray-500 hover:text-purple-700 text-2xl" aria-label="Close sidebar">
            &times;
          </button>
        </div>
        <nav className="flex-1 overflow-y-auto">
          <ul className="space-y-2">
            {navItems.map(renderNavItem)}
          </ul>
        </nav>
      </div>
      {/* Desktop sidebar */}
      <aside className="hidden md:flex w-56 bg-white shadow-lg flex-col py-8 px-4 min-h-screen">
        <div className="mb-8 text-2xl font-bold text-purple-700 tracking-wide text-center">AI-Powered POS System</div>
        <nav className="flex-1 overflow-y-auto">
          <ul className="space-y-2">
            {navItems.map(renderNavItem)}
          </ul>
        </nav>
      </aside>
    </>
  );
}

export default Sidebar;
