import React, { useState } from 'react';
import { Link, useLocation } from 'react-router-dom';

const navItems = [
  { label: 'Dashboard', icon: 'üè†', path: '/' },
  { label: 'Customers', icon: 'üßë‚Äçü§ù‚Äçüßë', path: '/customers' },
  { label: 'Suppliers', icon: 'üöö', path: '/suppliers' },
  { label: 'Products', icon: 'üì¶', path: '/products' },
  { label: 'Stock In', icon: '‚ûï', path: '/stock-in' },
  { label: 'Stock Out (Sales)', icon: '‚ûñ', path: '/stock-out' },
  { label: 'Reports', icon: 'üìä', path: '/reports' },
  { 
    label: 'Settings', 
    icon: '‚öôÔ∏è',
    hasDropdown: true,
    subItems: [
      { label: 'General Settings', icon: 'üîß', path: '/settings/general' },
      { label: 'Add project', icon: 'üë•', path: '/add-product' },
      { label: 'System Preferences', icon: '‚ö°', path: '/settings/preferences' },
      { label: 'Backup & Restore', icon: 'üíæ', path: '/settings/backup' },
      { label: 'API Configuration', icon: 'üîó', path: '/settings/api' }
    ]
  },
];

function Sidebar({ open, onClose }) {
  const [openDropdown, setOpenDropdown] = useState(null);
  const location = useLocation();

  const toggleDropdown = (label) => {
    setOpenDropdown(openDropdown === label ? null : label);
  };

  const isActive = (path) => {
    return location.pathname === path;
  };

  const renderNavItem = (item) => {
    if (item.hasDropdown) {
      return (
        <li key={item.label}>
          <button
            onClick={() => toggleDropdown(item.label)}
            className="w-full flex items-center justify-between gap-3 px-3 py-2 rounded-lg text-gray-700 hover:bg-purple-100 hover:text-purple-700 font-medium transition"
          >
            <div className="flex items-center gap-3">
              <span className="text-lg">{item.icon}</span>
              <span>{item.label}</span>
            </div>
            <span className={`text-sm transition-transform duration-200 ${openDropdown === item.label ? 'rotate-180' : ''}`}>
              ‚ñº
            </span>
          </button>
          {openDropdown === item.label && (
            <ul className="ml-6 mt-2 space-y-1">
              {item.subItems.map((subItem) => (
                <li key={subItem.label}>
                  <Link
                    to={subItem.path}
                    onClick={onClose}
                    className={`flex items-center gap-3 px-3 py-2 rounded-lg text-sm transition ${
                      isActive(subItem.path)
                        ? 'bg-purple-100 text-purple-700'
                        : 'text-gray-600 hover:bg-purple-50 hover:text-purple-700'
                    }`}
                  >
                    <span className="text-sm">{subItem.icon}</span>
                    <span>{subItem.label}</span>
                  </Link>
                </li>
              ))}
            </ul>
          )}
        </li>
      );
    }

    return (
      <li key={item.label}>
        <Link
          to={item.path}
          onClick={onClose}
          className={`flex items-center gap-3 px-3 py-2 rounded-lg font-medium transition ${
            isActive(item.path)
              ? 'bg-purple-100 text-purple-700'
              : 'text-gray-700 hover:bg-purple-100 hover:text-purple-700'
          }`}
        >
          <span className="text-lg">{item.icon}</span>
          <span>{item.label}</span>
        </Link>
      </li>
    );
  };

  return (
    <>
      {/* Mobile sidebar (overlay) */}
      <div
        className={`fixed inset-y-0 left-0 z-40 w-56 bg-white shadow-lg flex flex-col py-8 px-4 transform transition-transform duration-200 md:hidden
          ${open ? 'translate-x-0' : '-translate-x-full'}`}
        style={{ willChange: 'transform' }}
      >
        <div className="mb-8 flex items-center justify-between">
          <span className="text-2xl font-bold text-purple-700 tracking-wide">StockMS</span>
          <button onClick={onClose} className="text-gray-500 hover:text-purple-700 text-2xl" aria-label="Close sidebar">
            &times;
          </button>
        </div>
        <nav className="flex-1 overflow-y-auto">
          <ul className="space-y-2">
            {navItems.map(renderNavItem)}
          </ul>
        </nav>
      </div>
      {/* Desktop sidebar */}
      <aside className="hidden md:flex w-56 bg-white shadow-lg flex-col py-8 px-4 min-h-screen">
        <div className="mb-8 text-2xl font-bold text-purple-700 tracking-wide text-center">AI-Powered POS System</div>
        <nav className="flex-1 overflow-y-auto">
          <ul className="space-y-2">
            {navItems.map(renderNavItem)}
          </ul>
        </nav>
      </aside>
    </>
  );
}

export default Sidebar;
