import React, { useState, useEffect } from 'react';

function SaleItems() {
  // Master (sale) state
  const [sale, setSale] = useState({
    sale_number: '',
    customer_id: '',
    user_id: '',
    store_id: '',
    subtotal: 0,
    tax_amount: 0,
    discount_amount: 0,
    total_amount: 0,
    payment_method: 'cash',
    payment_status: 'completed',
    notes: '',
    loyalty_points_earned: 0,
    loyalty_points_redeemed: 0,
    sale_date: '',
  });

  // Detail (items) state
  const [items, setItems] = useState([]);
  const [itemForm, setItemForm] = useState({
    product_id: '',
    product_name: '',
    quantity: 1,
    unit_price: 0,
    discount_amount: 0,
    product_sku: '',
    product_barcode: '',
  });

  const [message, setMessage] = useState('');
  const [messageType, setMessageType] = useState(''); // 'success' or 'error'
  const [loading, setLoading] = useState(false);

  // Example AI insights data
  const aiInsights = [
    {
      name: 'Iphone 12',
      current: 5,
      reorder: 20
    },
    {
      name: 'HP',
      current: 3,
      reorder: 15
    }
  ];

  // Add item to detail list
  const addItem = () => {
    if (!itemForm.product_id || !itemForm.product_name || !itemForm.quantity || !itemForm.unit_price) {
      setMessage('Please fill all required item fields.');
      setMessageType('error');
      return;
    }
    setItems([...items, { ...itemForm, id: Date.now() }]);
    setItemForm({
      product_id: '',
      product_name: '',
      quantity: 1,
      unit_price: 0,
      discount_amount: 0,
      product_sku: '',
      product_barcode: '',
    });
  };

  // Remove item from detail list
  const removeItem = (id) => {
    setItems(items.filter(item => item.id !== id));
  };

  // Handle master form change
  const handleSaleChange = (e) => {
    setSale({ ...sale, [e.target.name]: e.target.value });
  };

  // Handle detail form change
  const handleItemChange = (e) => {
    setItemForm({ ...itemForm, [e.target.name]: e.target.value });
  };

  // Submit master-detail form
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!sale.sale_number || !sale.customer_id || !sale.user_id || !sale.store_id || !sale.sale_date) {
      setMessage('Please fill all required sale fields.');
      setMessageType('error');
      return;
    }
    if (items.length === 0) {
      setMessage('Please add at least one sale item.');
      setMessageType('error');
      return;
    }
    setLoading(true);
    // Calculate totals
    const subtotal = items.reduce((sum, item) => sum + item.quantity * item.unit_price, 0);
    const total_discount = items.reduce((sum, item) => sum + Number(item.discount_amount || 0), 0);
    const total_amount = subtotal + Number(sale.tax_amount) - total_discount;
    const saleData = {
      sale: {
        ...sale,
        subtotal,
        discount_amount: total_discount,
        total_amount,
      },
      items: items.map(({ id, ...rest }) => rest),
    };
    try {
      // Replace with your API call
      // await fetch('/api/sales', { method: 'POST', headers: { 'Content-Type': 'application/json' }, body: JSON.stringify(saleData) });
      setMessage('Sale submitted successfully! (API call simulated)');
      setMessageType('success');
      setSale({
        sale_number: '',
        customer_id: '',
        user_id: '',
        store_id: '',
        subtotal: 0,
        tax_amount: 0,
        discount_amount: 0,
        total_amount: 0,
        payment_method: 'cash',
        payment_status: 'completed',
        notes: '',
        loyalty_points_earned: 0,
        loyalty_points_redeemed: 0,
        sale_date: '',
      });
      setItems([]);
    } catch (err) {
      setMessage('Failed to submit sale.');
      setMessageType('error');
    } finally {
      setLoading(false);
      setTimeout(() => setMessage(''), 3000);
    }
  };

  return (
    <div className="flex flex-col w-full min-h-screen space-y-4 sm:space-y-6">
      {/* Feedback Message */}
      {message && (
        <div className={`mb-4 p-3 rounded ${messageType === 'success' ? 'bg-green-100 text-green-800' : 'bg-red-100 text-red-800'}`}>
          {message}
        </div>
      )}
      {/* Header */}
      <div className="bg-white rounded-xl shadow p-4 sm:p-6 w-full">
        <div className="flex flex-col sm:flex-row items-center justify-between mb-4 sm:mb-6 gap-2 sm:gap-0">
          <h1 className="text-xl sm:text-2xl font-bold text-gray-800">Sales Management</h1>
        </div>
        <p className="text-gray-600 text-sm sm:text-base">
          Manage your sales and sale items efficiently. Use the AI panel below for smart recommendations.
        </p>
      </div>
      {/* AI Insights Panel */}
      <div className="bg-red-50 border border-red-200 rounded-xl p-4 sm:p-6 mb-4">
        <div className="flex items-center mb-2">
          <span className="text-red-500 text-xl mr-2">ðŸ¤–</span>
          <span className="font-semibold text-red-700 text-base">AI Sales Insights</span>
        </div>
        <div className="text-red-700 text-sm mb-3">
          {aiInsights.length} products are low in stock based on AI demand forecasting.
        </div>
        <div className="space-y-3">
          {aiInsights.map((alert, idx) => (
            <div key={idx} className="bg-white border border-red-200 rounded-lg p-3">
              <div className="font-semibold text-gray-800">{alert.name}</div>
              <div className="text-gray-600 text-xs">Current: {alert.current} units</div>
              <div className="text-red-600 text-xs font-medium">AI Reorder Suggestion: {alert.reorder} units</div>
            </div>
          ))}
        </div>
      </div>
      {/* Master-Detail Form */}
      <form onSubmit={handleSubmit} className="space-y-6">
        {/* Master Section */}
        <div className="bg-white p-4 rounded shadow">
          <h2 className="font-bold text-lg mb-2">Sale Information</h2>
          <div className="grid grid-cols-2 gap-4">
            <input name="sale_number" value={sale.sale_number} onChange={handleSaleChange} placeholder="Sale Number*" className="border p-2 rounded" />
            <input name="customer_id" value={sale.customer_id} onChange={handleSaleChange} placeholder="Customer ID*" className="border p-2 rounded" />
            <input name="user_id" value={sale.user_id} onChange={handleSaleChange} placeholder="User ID*" className="border p-2 rounded" />
            <input name="store_id" value={sale.store_id} onChange={handleSaleChange} placeholder="Store ID*" className="border p-2 rounded" />
            <input name="sale_date" value={sale.sale_date} onChange={handleSaleChange} placeholder="Sale Date*" type="datetime-local" className="border p-2 rounded" />
            <select name="payment_method" value={sale.payment_method} onChange={handleSaleChange} className="border p-2 rounded">
              <option value="cash">Cash</option>
              <option value="card">Card</option>
              <option value="digital">Digital</option>
              <option value="gift_card">Gift Card</option>
              <option value="store_credit">Store Credit</option>
            </select>
            <input name="notes" value={sale.notes} onChange={handleSaleChange} placeholder="Notes" className="border p-2 rounded col-span-2" />
          </div>
        </div>
        {/* Detail Section */}
        <div className="bg-white p-4 rounded shadow">
          <h2 className="font-bold text-lg mb-2">Sale Items</h2>
          <div className="grid grid-cols-6 gap-2 mb-2">
            <input name="product_id" value={itemForm.product_id} onChange={handleItemChange} placeholder="Product ID*" className="border p-2 rounded" />
            <input name="product_name" value={itemForm.product_name} onChange={handleItemChange} placeholder="Product Name*" className="border p-2 rounded" />
            <input name="quantity" type="number" value={itemForm.quantity} onChange={handleItemChange} placeholder="Qty*" className="border p-2 rounded" min="1" />
            <input name="unit_price" type="number" value={itemForm.unit_price} onChange={handleItemChange} placeholder="Unit Price*" className="border p-2 rounded" min="0" step="0.01" />
            <input name="discount_amount" type="number" value={itemForm.discount_amount} onChange={handleItemChange} placeholder="Discount" className="border p-2 rounded" min="0" step="0.01" />
            <button type="button" onClick={addItem} className="bg-green-500 text-white rounded px-2">Add</button>
          </div>
          <table className="min-w-full text-xs sm:text-sm">
            <thead>
              <tr>
                <th>Product ID</th>
                <th>Name</th>
                <th>Qty</th>
                <th>Unit Price</th>
                <th>Discount</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {items.map(item => (
                <tr key={item.id}>
                  <td>{item.product_id}</td>
                  <td>{item.product_name}</td>
                  <td>{item.quantity}</td>
                  <td>{item.unit_price}</td>
                  <td>{item.discount_amount}</td>
                  <td>
                    <button type="button" onClick={() => removeItem(item.id)} className="text-red-500">Remove</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
        {/* Submit */}
        <button type="submit" className="bg-purple-600 text-white px-4 py-2 rounded" disabled={loading}>{loading ? 'Submitting...' : 'Submit Sale'}</button>
      </form>
    </div>
  );
}

export default SaleItems;
