import React, { useState } from 'react';

const navItems = [
  { label: 'Dashboard', icon: '🏠' },
  { label: 'Customers', icon: '🧑‍🤝‍🧑' },
  { label: 'Suppliers', icon: '🚚' },
  { label: 'Products', icon: '📦' },
  { label: 'Stock In', icon: '➕' },
  { label: 'Stock Out (Sales)', icon: '➖' },
  { label: 'Reports', icon: '📊' },
  { label: 'Settings', icon: '⚙️', dropdown: [
    { label: 'Add Categories', icon: '#' },
    { label: 'Account', href: '#' },
    { label: 'Logout', href: '#' },
  ] },
];

function Sidebar({ open, onClose }) {
  const [settingsOpen, setSettingsOpen] = useState(false);

  // Helper to render nav item (with dropdown if needed)
  const renderNavItem = (item) => {
    if (item.label === 'Settings') {
      return (
        <div className="relative">
          <button
            type="button"
            className="flex items-center gap-3 px-3 py-2 rounded-lg text-gray-700 hover:bg-purple-100 hover:text-purple-700 font-medium transition w-full focus:outline-none"
            onClick={() => setSettingsOpen((open) => !open)}
          >
            <span className="text-lg">{item.icon}</span>
            <span>{item.label}</span>
            <svg className={`ml-auto w-4 h-4 transition-transform ${settingsOpen ? 'rotate-180' : ''}`} fill="none" stroke="currentColor" strokeWidth="2" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" d="M19 9l-7 7-7-7" /></svg>
          </button>
          {settingsOpen && (
            <ul className="absolute left-0 mt-1 w-40 bg-white border border-gray-200 rounded shadow-lg z-50">
              {item.dropdown.map((option) => (
                <li key={option.label}>
                  <a
                    href={option.href}
                    className="block px-4 py-2 text-gray-700 hover:bg-purple-100 hover:text-purple-700 text-sm"
                  >
                    {option.label}
                  </a>
                </li>
              ))}
            </ul>
          )}
        </div>
      );
    }
    return (
      <a
        href="#"
        className="flex items-center gap-3 px-3 py-2 rounded-lg text-gray-700 hover:bg-purple-100 hover:text-purple-700 font-medium transition"
      >
        <span className="text-lg">{item.icon}</span>
        <span>{item.label}</span>
      </a>
    );
  };

  return (
    <>
      {/* Mobile sidebar (overlay) */}
      <div
        className={`fixed inset-y-0 left-0 z-40 w-56 bg-white shadow-lg flex flex-col py-8 px-4 transform transition-transform duration-200 md:hidden
          ${open ? 'translate-x-0' : '-translate-x-full'}`}
        style={{ willChange: 'transform' }}
      >
        <div className="mb-8 flex items-center justify-between">
          <span className="text-2xl font-bold text-purple-700 tracking-wide">StockMS</span>
          <button onClick={onClose} className="text-gray-500 hover:text-purple-700 text-2xl" aria-label="Close sidebar">
            &times;
          </button>
        </div>
        <nav className="flex-1">
          <ul className="space-y-2">
            {navItems.map((item) => (
              <li key={item.label}>{renderNavItem(item)}</li>
            ))}
          </ul>
        </nav>
      </div>
      {/* Desktop sidebar */}
      <aside className="hidden md:flex w-56 bg-white shadow-lg flex-col py-8 px-4 min-h-screen">
        <div className="mb-8 text-2xl font-bold text-purple-700 tracking-wide text-center">AI-Powered POS System</div>
        <nav className="flex-1">
          <ul className="space-y-2">
            {navItems.map((item) => (
              <li key={item.label}>{renderNavItem(item)}</li>
            ))}
          </ul>
        </nav>
      </aside>
    </>
  );
}

export default Sidebar;
