import React from 'react';

const navItems = [
  { label: 'Dashboard', icon: 'üè†' },
  { label: 'Customers', icon: 'üßë‚Äçü§ù‚Äçüßë' },
  { label: 'Suppliers', icon: 'üöö' },
  { label: 'Products', icon: 'üì¶' },
  { label: 'Stock In', icon: '‚ûï' },
  { label: 'Stock Out (Sales)', icon: '‚ûñ' },
  { label: 'Reports', icon: 'üìä' },
  { label: 'Settings', icon: '‚öôÔ∏è' },
];

function Sidebar() {
  return (
    <aside className="w-56 bg-white shadow-lg flex flex-col py-8 px-4 min-h-screen">
      <div className="mb-8 text-2xl font-bold text-purple-700 tracking-wide text-center">StockMS</div>
      <nav className="flex-1">
        <ul className="space-y-2">
          {navItems.map((item) => (
            <li key={item.label}>
              <a
                href="#"
                className="flex items-center gap-3 px-3 py-2 rounded-lg text-gray-700 hover:bg-purple-100 hover:text-purple-700 font-medium transition"
              >
                <span className="text-lg">{item.icon}</span>
                <span>{item.label}</span>
              </a>
            </li>
          ))}
        </ul>
      </nav>
    </aside>
  );
}

export default Sidebar;
