import React from 'react';

function Categories() {
  // Placeholder categories data
  const categories = [
    { id: 1, name: 'Clothes', description: 'All clothing items' },
    { id: 2, name: 'Perfume', description: 'Perfume and fragrances' },
    { id: 3, name: 'Accessories', description: 'Fashion accessories' },
  ];

  return (
    <div className="flex flex-col flex-1 w-full min-h-screen p-4">
      <div className="bg-white rounded-xl shadow p-6 mb-6 w-full">
        <h1 className="text-2xl font-bold text-gray-800 mb-4">Categories</h1>
        <table className="min-w-full text-sm">
          <thead>
            <tr className="text-left text-gray-500">
              <th className="py-2 px-3">ID</th>
              <th className="py-2 px-3">Name</th>
              <th className="py-2 px-3">Description</th>
            </tr>
          </thead>
          <tbody>
            {categories.map((cat) => (
              <tr key={cat.id} className="border-t">
                <td className="py-2 px-3 whitespace-nowrap">{cat.id}</td>
                <td className="py-2 px-3 whitespace-nowrap">{cat.name}</td>
                <td className="py-2 px-3 whitespace-nowrap">{cat.description}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default Categories;
